import React from "react";
import "./Arrow.css";

const Arrow = ({ angle = 0, size = 20, color = "black" }) => {
  // Calculate rotation based on angle
  const rotation = `rotate(${angle}deg)`;

  return (
    <div className="arrow" style={{ transform: rotation, borderColor: color }}>
      <svg width={size} height={size}>
        <polygon points={`${size / 2},0 0,${size} ${size},${size}`} />
      </svg>
    </div>
  );
};

export default Arrow;
