import { useState } from "react";

function GearSystem(props) {
  const midGear = Math.ceil(props.gearLevels / 2);
  const [currentGear, setCurrentGear] = useState(midGear);

  const handleClick = (event) => {
    setCurrentGear(Number(event.target.textContent));
    alert(currentGear);
  };

  const gearLevels = [...Array(props.numberOfGearLevels).keys()];
  return (
    <>
      {gearLevels.map((gearNumber) => (
        <button onClick={handleClick}>{gearNumber}</button>
      ))}
      {gearLevels.map((gearNumber) => {
        <li>{gearNumber}</li>;
      })}
    </>
  );
}

export default GearSystem;
