import React, { useState } from "react";

function EditAndSetButton({ onToggle, isEditing }) {
  return <button onClick={onToggle}>{isEditing ? "Set" : "Edit"}</button>;
}
function LengthAndWidthForm() {
    const [isEditing, setIsEditing] = useState(false);
    const [length, setLength] = useState("");
    const [width, setWidth] = useState("");

    const handleToggle = () => {
      setIsEditing(!isEditing);
    };

    return (
      <form onSubmit={(e) => e.preventDefault()}>
        <label>
          Length:
          <input
            type="number"
            value={length}
            onChange={(e) => setLength(e.target.value)}
            readOnly={!isEditing}
          />
        </label>
        <label>
          Width:
          <input
            type="number"
            value={width}
            onChange={(e) => setWidth(e.target.value)}
            readOnly={!isEditing}
          />
        </label>
        <EditAndSetButton onToggle={handleToggle} isEditing={isEditing} />
      </form>
    );
  }

function MapArea() {
  const circleSize = 8;
  const circleSpacing = 25;
  const gridSize = 5;

  const circles = [];
  for (let i = 0; i < gridSize; i++) {
    for (let j = 0; j < gridSize; j++) {
      circles.push(
        <div
          key={`circle-${i}-${j}`}
          style={{
            position: "absolute",
            left: `${j * circleSpacing}px`,
            top: `${i * circleSpacing}px`,
            width: `${circleSize}px`,
            height: `${circleSize}px`,
            borderRadius: "50%",
            backgroundColor: "white",
            border: "1px solid black",
            display: "inline",
          }}
        />
      );
    }
  }

  const mapArea = (
    <div
      style={{
        position: "relative",
        width: "500px",
        height: "500px",
        backgroundColor: "lightgray",
        overflow: "hidden",
      }}
    >
      {circles}
    </div>
  );

  return <>{mapArea}</>;
}

function Map() {
  return (
    <>
      <LengthAndWidthForm />
      <MapArea />
    </>
  );
}

export default Map;
