import React, { useState } from "react";

function MapArea() {
  const [showCircles, setShowCircles] = useState(true);
  const toggleCircles = () => {
    setShowCircles(!showCircles);
  };

  const circleSize = 8;
  const circleSpacing = 25;
  const gridSize = 20;

  const circles = [];
  for (let i = 0; i < gridSize; i++) {
    for (let j = 0; j < gridSize; j++) {
      circles.push(
        <div
          key={`circle-${i}-${j}`}
          style={{
            position: "absolute",
            left: `${j * circleSpacing}px`,
            top: `${i * circleSpacing}px`,
            width: `${circleSize}px`,
            height: `${circleSize}px`,
            borderRadius: "50%",
            backgroundColor: "white",
            border: "1px solid black",
            display: showCircles ? "inline" : "none",
          }}
        />
      );
    }
  }

  const mapArea = (
    <div
      style={{
        position: "relative",
        width: "500px",
        height: "500px",
        backgroundColor: "lightgray",
        overflow: "hidden",
      }}
    >
      {circles}
    </div>
  );

  return <>{mapArea}</>;
}

function Map() {
  const lengthAndWidth = (
    <form>
      <label>
        Length:
        <input type="number" name="length" />
      </label>
      <label>
        Width:
        <input type="number" name="width" />
      </label>
      <input type="submit" value="Submit" />
    </form>
  );
  let mapArea = <MapArea />;
  return (
    <>
      {lengthAndWidth}
      {mapArea}
    </>
  );
}

export default Map;
