import React, { useState } from "react";
import Arrow from "./Arrow";

import Graph from "./graph";

function EditAndSetButton({ onToggle, isEditing }) {
  return <button onClick={onToggle}>{isEditing ? "Set" : "Edit"}</button>;
}

function LengthAndWidthForm({ onChange }) {
  const [isEditing, setIsEditing] = useState(false);
  const [length, setLength] = useState("5");
  const [width, setWidth] = useState("5");

  const handleToggle = () => {
    setIsEditing(!isEditing);
    if (isEditing) {
      onChange({ length: Number(length), width: Number(width) });
    }
  };

  return (
    <form onSubmit={(e) => e.preventDefault()}>
      <label>
        Length:
        <input
          type="number"
          value={length}
          onChange={(e) => setLength(e.target.value)}
          readOnly={!isEditing}
        />
      </label>
      <label>
        Width:
        <input
          type="number"
          value={width}
          onChange={(e) => setWidth(e.target.value)}
          readOnly={!isEditing}
        />
      </label>
      <EditAndSetButton onToggle={handleToggle} isEditing={isEditing} />
    </form>
  );
}

function MapArea({ gridLength, gridWidth }) {
  const circleSize = 15;
  const circleSpacing = 30;
  let graph = new Graph(gridLength, gridWidth);

  const handleCircleClick = (event) => {
    const isOpaque = event.target.getAttribute("data-is-opaque") === "true";
    event.target.setAttribute("data-is-opaque", (!isOpaque).toString());
    event.target.style.opacity = isOpaque ? 0.3 : 1;
  };

  const circles = [];
  for (let i = 0; i < gridLength; i++) {
    for (let j = 0; j < gridWidth; j++) {
      circles.push(
        <div
          key={`circle-${i}-${j}`}
          data-key={`${i}-${j}`}
          data-is-opaque="false"
          style={{
            position: "absolute",
            left: `${j * circleSpacing}px`,
            top: `${i * circleSpacing}px`,
            width: `${circleSize}px`,
            height: `${circleSize}px`,
            borderRadius: "50%",
            backgroundColor: "white",
            border: "1px solid black",
            cursor: "pointer",
            opacity: 0.3,
          }}
        />
      );
    }
  }
  circles.push(<Arrow angle={180}/>);

  return (
    <div
      style={{
        position: "relative",
        width: "630px",
        height: "630px",
        backgroundColor: "lightgray",
        overflow: "hidden",
      }}
      onClick={handleCircleClick}
    >
      {circles}
    </div>
  );
}

function Map() {
  const [dimensions, setDimensions] = useState({ length: 5, width: 5 });

  return (
    <>
      <LengthAndWidthForm onChange={setDimensions} />
      <MapArea gridLength={dimensions.length} gridWidth={dimensions.width} />
    </>
  );
}

export default Map;
